mapper.insert:

        SELECT LAST_INSERT_ID()

insert into dish (name, category_id, price, image, description, create_time, update_time, create_user, update_user, status) values (
        #{name}, #{categoryId}, #{price}, #{image}, #{description}, #{createTime}, #{updateTime}, #{createUser}, #{updateUser}, #{status}
)

mapper.select:
select d.*, c.name categoryName from dish d left join category c on d.category_id = c.id
d.name like concat('%', #{name}, '%'), and d.category_id = #{categoryId}, d.status = #{status}
order by d.create_time desc

mapper.delete:
delete from dish
where id in

        #{id}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
id = #{id}

mapper.select:
select d.id, d.name, category_id, price, image, description, d.status, d.create_time, d.update_time, d.create_user, d.update_user from
dish d left join category c on d.category_id = c.id

        d.category_id = #{categoryId}
